ull, "nuid": "35e5fd67-6d18-437e-8c19-d60e44d0a216", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 2, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}, {"bindings": {}, "collapsed": false, "command": "// SETUP\nimport org.apache.spark.sql.SparkSession\ndef realityCheck(testMethod:(SparkSession, String, String, String) => DataFrame, spark:SparkSession, investorsPath:String, assetsPath:String, targetDirectory:String): Unit = {\n  import org.apache.spark.sql.functions._\n  import org.apache.spark.sql.types._\n\n  val resultDF = testMethod(spark, investorsPath, assetsPath, targetDirectory)\n    \n  val investorsDF = (spark.read.format(\"delta\").load(investorsPath)\n                     .select(\"investor_id\", \"name\", \"credit_score\", \"risk_score\", \"fav_stocks\"))\n\n  val exp = (investorsDF\n         .withColumn(\"fav_stocks\", split(col(\"fav_stocks\"), \",\"))\n         .select($\"*\", explode($\"fav_stocks\").alias(\"asset_id\")).drop($\"fav_stocks\")\n        )\n\n   val assets = spark.read.format(\"delta\").load(assetsPath)\n\n   val result =  (exp.as(\"exp\").join(assets.as(\"assets\"), $\"exp.asset_id\" === $\"assets.asset_id\", \"left\")\n                  .select($\"exp.*\", $\"assets.asset_risk\")) \n  \n  val correctDF = result \n  \n  \n  def read_delta() : DataFrame = {\n      try {\n        spark.read.format(\"delta\").load(targetDirectory)\n      } catch { \n        case _: Throwable => spark.emptyDataFrame \n      }  \n  }\n  \n  val deltaTable = read_delta()\n\n  val tests = new TestSuite()\n  tests.addTest(TestCase(id=\"PCF-10S-A\", description=\"Returns correct schema\",\n           testFunction = () => compareSchemas(resultDF.schema, correctDF.schema, testColumnOrder=false, testNullable=false)))\n  tests.addTest(TestCase(id=\"PCF-10S-B\", description=\"Returns DataFrame with correct number of rows\",              \n           testFunction = () => resultDF.count == correctDF.count))\n  tests.addTest(TestCase(id=\"PCF-10S-C\", description=\"Returns DataFrame with correct results\", \n           testFunction = () => compareDataFramesLimited(resultDF, correctDF)))\n  tests.addTest(TestCase(id=\"PCF-10S-D\", description = \"Delta table in place\",\n           testFunction = () => !deltaTable.isEmpty))\n  tests.addTest(TestCase(id=\"PCF-10S-E\",description = \"Delta table has correct content\",\n           testFunction = () => compareDataFramesLimited(deltaTable, correctDF)))\n\n  \n  tests.displayResults()\n\n}\n  \ndisplayHTML(\"\"\"\nDeclared the following function:\n  <li><span style=\"color:green; font-weight:bold\">realityCheck</span></li>\n\"\"\")", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "a50964cf-a0a6-40c2-bc4a-e24f14533aed", "height": "auto", "hideCommandCode": false, "hideCommandResult": false, "iPythonMetadata": null, "inputWidgets": {}, "latestUser": "", "latestUserId": null, "nuid": "2d47400c-0d98-48d8-873a-30f9efa426ac", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 3, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}, {"bindings": {}, "collapsed": false, "command": "", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffIns