on": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "d8c6b36a-9195-4df6-ac7b-75f791d0932e", "height": "auto", "hideCommandCode": false, "hideCommandResult": false, "iPythonMetadata": null, "inputWidgets": {}, "latestUser": "", "latestUserId": null, "nuid": "de0a267b-576e-4ac6-8aa1-430771c1b2b2", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 17, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}, {"bindings": {}, "collapsed": false, "command": "def testAssertDbrVersion(): Boolean = {\n  \n  val (majorVersion, minorVersion) = getDbrMajorAndMinorVersions()\n  val major = majorVersion.toInt\n  val minor = minorVersion.toInt\n\n  val goodVersions = Seq(\n    (\"SG1\", major-1, minor-1),\n    (\"SG2\", major-1, minor),\n    (\"SG3\", major, minor-1),\n    (\"SG4\", major, minor)\n  )\n  \n  for ( (name, testMajor, testMinor) <- goodVersions) {\n    println(f\"-- ${name} ${testMajor}.${testMinor}\")\n    assertDbrVersion(null, testMajor, testMinor, false)\n    println(f\"-\"*80)\n  }\n\n  val badVersions = Seq(\n    (\"SB1\", major+1, minor+1),\n    (\"SB2\", major+1, minor),\n    (\"SB3\", major, minor+1)\n  )\n\n  for ( (name, testMajor, testMinor) <- badVersions) {\n    try {\n      println(f\"-- ${name} ${testMajor}.${testMinor}\")\n      assertDbrVersion(null, testMajor, testMinor, false)\n      throw new Exception(\"Expected AssertionError\")\n    } catch {\n      case e: AssertionError => println(e)\n    }\n    println(f\"-\"*80)\n  }\n  return true\n}\nfunctionPassed(testAssertDbrVersion())", "commandTitle": "", "commandType": "auto",