seName == expectedDatabaseName)\n  \n  val actualDatabaseName = createUserDatabase(courseType, username, moduleName, lessonName)\n  assert(actualDatabaseName == expectedDatabaseName)\n\n  // Spark 2 uses \"database\" column and Spark 3 uses \"namespace\"\n  val SPARK_SCALA_VERSION = sys.env(\"SPARK_SCALA_VERSION\")\n  val columnName = if (SPARK_SCALA_VERSION == \"2.12\") \"namespace\" else if (SPARK_SCALA_VERSION == \"2.11\") \"databaseName\" else \"unknown\"\n  \n  assert(spark.sql(s\"SHOW DATABASES LIKE '$expectedDatabaseName'\").first.getAs[String](columnName) == expectedDatabaseName)\n  assert(spark.sql(\"SELECT current_database()\").first.getAs[String](\"current_database()\") == expectedDatabaseName)\n  \n  return true\n}\nfunctionPassed(testCreateUserDatabase())", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "7e1772d5-8ee3-4982-8017-5e06587074b3", "height": "auto", "hideCommandCode": false, "hideCommandResult": false, "iPythonMetadata": null, "inputWidgets": {}, "latestUser": "", "latestUserId": null, "nuid": "f20e5c1d-a5c3-41e2-9c4c-f28e463ae2f9", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 23, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}, {"bindings": {}, "collapsed": false, "command": "%md\n## Test `getExperimentId()`", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "6c5abb26-a54b-4764-9940-d1ec7fe17dc6", "height": "auto", "hideCommandCode": false, "hideCommandResult": false, "iPythonMetadata": null, "inputWidgets": {}, "latestUser": "", "latestUserId": null, "nuid": "71e84359-1d09-4859-9264-9dca786314c4", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 24, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}, {"bindings": {}, "collapsed": false, "command": "def testGetExperimentId(): Boolean = {\n  \n  import scala.collection.mutable.ArrayBuffer\n\n  val testsPassed = ArrayBuffer.empty[Boolean]\n  \n  def passedTest(result: Boolean, message: String = null) = {\n    if (result) {\n      testsPassed += true\n    } else {\n      testsPassed += false\n      println(s\"Failed Test: $message\")\n    } \n  }\n  \n  // Test that result is correct type\n  try {\n    assert(getExperimentId().isInstanceOf[Long])\n    passedTest(true)\n  } catch {\n    case e: Exception => passedTest(false, \"result of getExperimentId is not of Long type\")\n  }\n  \n  // Note that the tags are an immutable map so we can't test other values\n  // Another option is add parameters to getExperimentId, but that could break existing uses\n  \n  // Test that result comes out as expected\n  try {\n    val notebookId = try {\n      com.databricks.logging.AttributionContext.current.tags(com.databricks.logging.TagDefinition(\"notebookId\", \"\"))\n    } catch {\n      case e: Exception => null\n    }\n    val jobId = try {\n      com.databricks.logging.AttributionContext.current.tags(com.databricks.logging.TagDefinition(\"jobId\", \"\"))\n    } catch {\n      case e: Exception => null\n    }\n    val expectedResult = if (notebookId != null){\n      notebookId.toLong\n    } else {\n      if (jobId != null) {\n        jobId.toLong\n      } else {\n        0\n      }\n    }\n    assert(expectedResult == getExperimentId())\n    passedTest(true)\n  } catch {\n    case e: Exception => passedTest(false, \"unexpected result for getExperimentId\")\n  }\n  \n  val numTestsPassed = testsPassed.groupBy(identity).mapValues(_.size)(true)\n  if (numTestsPassed == testsPassed.length) {\n    println(s\"All $numTestsPassed tests for getExperimentId passed\")\n    true\n  } else {\n    throw new Exception(s\"$numTestsPassed of ${testsPassed.length} tests for getExperimentId passed\")\n  }\n}\n\nfunctionPassed(testGetExperimentId())", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "6b18485b-95e2-4fb4-9386-99bd650de090", "height": "auto", "hideCommandCode": false, "hideCommandResult": false, "iPythonMetadata": null, "inputWidgets": {}, "latestUser": "", "latestUserId": null, "nuid": "aff4631d-b87e-417a-845c-fd37cb148cd1", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 25, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}, {"bindings": {}, "collapsed": false, "command": "%md\n## Test `classroomCleanup()`", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "231c12ae-5361-4032-99c8-b88f24acf767", "height