, "command": "// SETUP\n\nimport org.apache.spark.sql.SparkSession\n\ndef realityCheck(testMethod:(SparkSession, String, String, String) => DataFrame, spark:SparkSession, investorsPath:String, stockTransactionsPath:String, targetDirectory:String): Unit = {\n  \n  import org.apache.spark.sql.functions._\n  import org.apache.spark.sql.types._\n  import java.io.ByteArrayOutputStream\n  \n  val resultDF = testMethod(spark, investorsPath, stockTransactionsPath, targetDirectory)\n  \n  val investors = (spark.read.format(\"delta\").load(investorsPath).select(\"investor_id\",\"name\"))\n  \n  val stock_transactions = (spark.read.format(\"delta\").load(stockTransactionsPath))\n\n  val correctDF = (stock_transactions.join(investors, (investors(\"investor_id\") === stock_transactions(\"investor\")), \"left\")\n         .select($\"investor\", $\"name\", $\"spend_balance\", $\"order_timestamp\".cast(\"date\").alias(\"date\"))\n         .groupBy(\"date\", \"investor\", \"name\")\n         .agg(round(sum(\"spend_balance\")).alias(\"daily_win\"))\n         .orderBy(desc(\"daily_win\"))\n         .limit(10)\n         )\n  \n  val resultOutput = new ByteArrayOutputStream\n  val correctOutput = new ByteArrayOutputStream\n  \n  def read_delta() : DataFrame = {\n      try {\n        spark.read.format(\"delta\").load(targetDirectory)\n      } catch { \n        case _: Throwable => spark.emptyDataFrame \n      }\n  }\n  \n  val deltaTable = read_delta()\n  \n  val tests = new TestSuite()\n  \n  tests.addTest(TestCase(id=\"PCF-17G-A\", description = \"Returns correct schema\",\n           testFunction = () => compareSchemas(resultDF.schema, correctDF.schema, testColumnOrder=false, testNullable=true)))\n  tests.addTest(TestCase(id=\"PCF-17G-B\", description = \"Returns DataFrame with correct number of rows\",              \n           testFunction = () => resultDF.count == correctDF.count))\n  tests.addTest(TestCase(id=\"PCF-17G-C\", description=\"Returns DataFrame with correct results\", \n           testFunction = () => compareDataFramesLimited(resultDF, correctDF)))\n  tests.addTest(TestCase(id=\"PCF-17G-E\", description = \"Delta Table in place\",\n         testFunction = () => !deltaTable.isEmpty))\n  tests.addTest(TestCase(id=\"PCF-17G-G\", description = \"Delta Table has correct content\",\n           testFunction = () => compareDataFramesLimited(deltaTable, correctDF)))\n \n tests.displayResults()\n  \n}\n  \ndisplayHTML(\"\"\"\nDeclared the following function:\n  <li><span style=\"color:green; font-weight:bold\">realityCheck</span></li>\n\"\"\")\n", "commandTitle": "", "commandType": "auto", "commandVersion": 0, "commentThread": [], "commentsVisible": false, "customPlotOptions": {}, "datasetPreviewNameToCmdIdMap": {}, "diffDeletes": [], "diffInserts": [], "displayType": "table", "error": null, "errorSummary": null, "finishTime": 0, "globalVars": {}, "guid": "3344bbf2-c7be-4eb4-bdaa-be8ad28f4a33", "height": "auto", "hideCommandCode": false, "hideCommandResult": false, "iPythonMetadata": null, "inputWidgets": {}, "latestUser": "", "latestUserId": null, "nuid": "68745915-cfb6-48e0-85fc-56395d161f0c", "origId": 0, "parentHierarchy": [], "pivotAggregation": null, "pivotColumns": null, "position": 7, "results": null, "showCommandTitle": false, "startTime": 0, "state": "finished", "streamStates": {}, "submitTime": 0, "subtype": "command", "version": "CommandV1", "width": "auto", "workflows": [], "xColumns": null, "yColumns": null}], "dashboards": [], "globalVars": {}, "guid": "5a79a386-b2c3-4511-abb8-132dceb531f7", "iPythonMetadata": null, "inputWidgets": {}, "language": "scala", "name": "PCF-17G-ClassroomSetup", "origId": 0, "version": "NotebookV1"}